import React, { useState, useEffect } from 'react';

const ServiceHeroSection = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [primaryButtonHover, setPrimaryButtonHover] = useState(false);
  const [secondaryButtonHover, setSecondaryButtonHover] = useState(false);
  const [topBadgeHover, setTopBadgeHover] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  const handleContactClick = () => {
    window.location.href = '/contact-us';
  };

  const getResponsiveStyles = () => {
    const width = window.innerWidth;
    
    // Small Mobile
    if (width <= 400) {
      return {
        minHeight: '90vh',
        padding: '90px 20px 0px 20px'
      };
    }
    // Mobile
    else if (width <= 768) {
      return {
        minHeight: '70vh',
        padding: '40px 20px 0px 20px'
      };
    }
    // Tablet
    else if (width <= 1024) {
      return {
        minHeight: '90vh',
        padding: '100px 20px 40px 20px'
      };
    }
    // Desktop
    else {
      return {
        minHeight: '100vh',
        padding: '80px 20px 40px 20px'
      };
    }
  };

  const responsiveStyles = getResponsiveStyles();

  const containerStyle = {
    position: 'relative',
    width: '100%',
    minHeight: responsiveStyles.minHeight,
    background: 'linear-gradient(180deg, #04091D 16.18%, #0D98BA 219.08%)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: responsiveStyles.padding,
    overflow: 'hidden',
    boxSizing: 'border-box'
  };

  const contentContainerStyle = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    gap: '23px',
    maxWidth: '862px',
    width: '100%',
    opacity: isVisible ? 1 : 0,
    transform: isVisible ? 'translateY(0)' : 'translateY(20px)',
    transition: 'all 0.8s ease-out',
    boxSizing: 'border-box',
    padding: window.innerWidth <= 768 ? '0 10px' : '0'
  };

  const topButtonStyle = {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    padding: window.innerWidth <= 768 ? '8px 20px' : '10px 24px',
    background: 'linear-gradient(90deg, #FFFFFF 0%, #85CCDD 52.41%, #0D98BA 81.55%, #0B829F 95.26%)',
    border: '1px solid #0D94BB',
    boxShadow: topBadgeHover ? '0px 0px 20px 4px rgba(13, 148, 187, 0.9)' : '0px 0px 12px 2px rgba(13, 148, 187, 0.7)',
    borderRadius: '25px',
    cursor: 'pointer',
    opacity: isVisible ? 1 : 0,
    transform: topBadgeHover ? 'scale(1.05)' : (isVisible ? 'scale(1)' : 'scale(0.9)'),
    transition: 'all 0.3s ease',
    boxSizing: 'border-box'
  };

  const topButtonTextStyle = {
    fontFamily: 'Poppins, sans-serif',
    fontWeight: 700,
    fontSize: window.innerWidth <= 768 ? '14px' : 'clamp(14px, 2vw, 16px)',
    lineHeight: '24px',
    letterSpacing: '0.02em',
    color: '#04091D',
    margin: 0
  };

  const mainHeadingStyle = {
    fontFamily: 'Poppins, sans-serif',
    fontWeight: 600,
    fontSize: 'clamp(32px, 6vw, 65px)',
    lineHeight: '1.1',
    textAlign: 'center',
    color: '#FFFFFF',
    textShadow: '0px 4px 4px rgba(0, 0, 0, 0.25)',
    margin: 0,
    width: '100%',
    maxWidth: window.innerWidth <= 768 ? '100%' : '1100px',
    opacity: isVisible ? 1 : 0,
    transform: isVisible ? 'translateY(0)' : 'translateY(30px)',
    transition: 'all 0.8s ease-out 0.4s',
    padding: window.innerWidth <= 768 ? '0 5px' : '0'
  };

  const firstLineStyle = {
    whiteSpace: window.innerWidth <= 768 ? 'normal' : 'nowrap',
    display: 'inline-block'
  };

  const highlightStyle = {
    color: '#85CCDD'
  };

  const subHeadingStyle = {
    fontFamily: 'Lato, sans-serif',
    fontWeight: 600,
    fontSize: 'clamp(14px, 2vw, 16px)',
    lineHeight: '1.4',
    textAlign: 'center',
    color: '#FFFFFF',
    margin: 0,
    width: '100%',
    opacity: isVisible ? 1 : 0,
    transition: 'all 0.8s ease-out 0.6s',
    padding: window.innerWidth <= 768 ? '0 10px' : '0'
  };

  const dividerStyle = {
    width: '100%',
    maxWidth: '654px',
    height: '1px',
    background: '#0D98BA',
    borderRadius: '10px',
    opacity: isVisible ? 1 : 0,
    transform: isVisible ? 'scaleX(1)' : 'scaleX(0)',
    transition: 'all 0.8s ease-out 0.8s'
  };

  const featuresContainerStyle = {
    display: 'flex',
    flexDirection: 'row',
    flexWrap: 'wrap',
    alignItems: 'center',
    justifyContent: 'center',
    gap: window.innerWidth <= 768 ? '15px' : '23px',
    opacity: isVisible ? 1 : 0,
    transition: 'all 0.8s ease-out 1s',
    padding: window.innerWidth <= 768 ? '0 10px' : '0'
  };

  const featureItemStyle = {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    gap: '10px'
  };

  const featureIconStyle = {
    width: '25px',
    height: '25px',
    flexShrink: 0,
    objectFit: 'contain'
  };

  const featureTextStyle = {
    fontFamily: 'Lato, sans-serif',
    fontWeight: 400,
    fontSize: 'clamp(12px, 1.5vw, 14px)',
    lineHeight: '17px',
    letterSpacing: '0.02em',
    color: '#FFFFFF',
    margin: 0,
    whiteSpace: window.innerWidth <= 480 ? 'normal' : 'nowrap'
  };

  const buttonsContainerStyle = {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'flex-start',
    gap: '23px',
    flexWrap: window.innerWidth <= 480 ? 'wrap' : 'nowrap',
    justifyContent: 'center',
    opacity: isVisible ? 1 : 0,
    transition: 'all 0.8s ease-out 1.2s'
  };

  const primaryButtonStyle = {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '10px 24px',
    background: primaryButtonHover ? '#0B829F' : '#0D98BA',
    boxShadow: primaryButtonHover ? '0px 6px 12px rgba(0, 0, 0, 0.4)' : '0px 4px 4px rgba(0, 0, 0, 0.25)',
    borderRadius: '6px',
    border: 'none',
    cursor: 'pointer',
    transform: primaryButtonHover ? 'translateY(-2px)' : 'translateY(0)',
    transition: 'all 0.3s ease',
    boxSizing: 'border-box',
    minWidth: '157px',
    height: '48px'
  };

  const primaryButtonTextStyle = {
    fontFamily: 'Poppins, sans-serif',
    fontWeight: 500,
    fontSize: 'clamp(14px, 2vw, 16px)',
    lineHeight: '24px',
    letterSpacing: '0.02em',
    color: '#000000',
    margin: 0
  };

  const secondaryButtonStyle = {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '10px 24px',
    background: 'transparent',
    border: '2px solid #0D98BA',
    borderRadius: '6px',
    cursor: 'pointer',
    transform: secondaryButtonHover ? 'translateY(-2px)' : 'translateY(0)',
    transition: 'all 0.3s ease',
    boxSizing: 'border-box',
    minWidth: '157px',
    height: '48px'
  };

  const secondaryButtonTextStyle = {
    fontFamily: 'Lato, sans-serif',
    fontWeight: 600,
    fontSize: 'clamp(14px, 2vw, 16px)',
    lineHeight: '150%',
    letterSpacing: '0.02em',
    color: '#0D98BA',
    margin: 0,
    textAlign: 'center'
  };

  return (
    <div style={containerStyle}>
      <div style={contentContainerStyle}>
        <div 
          style={topButtonStyle}
          onMouseEnter={() => setTopBadgeHover(true)}
          onMouseLeave={() => setTopBadgeHover(false)}
        >
          <span style={topButtonTextStyle}>Built to Convert</span>
        </div>

        <h1 style={mainHeadingStyle}>
          {window.innerWidth <= 768 ? (
            <>
              Data-Driven CRO Services for Scalable Growth{' '}
              <span style={highlightStyle}>Revenue Growth</span>
            </>
          ) : (
